    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        //on clicking the send button
        
        
        String username,pass,passconf,oldpassw;
        username = jTextField1.getText();
        oldpassw = oldpass.getText();
        pass = jpass.getText();
        passconf = jpassconf.getText();
        
        LoginScreen.getStoredDetails(username);
        if(LoginScreen.usernameDb.equals(username) && LoginScreen.passDb.equals(oldpassw))
        {

        if(RegisterNew.confirmPass(pass, passconf)==true)
        {
           JOptionPane.showMessageDialog(null, "Password Changed Successfullyn" +
                        "Press OK to Continue", "Password Changed", JOptionPane.INFORMATION_MESSAGE); 
        }
        else
        {
            toptext1.setText("Password Do Not Match");
        }
        }
        else
            System.exit(0);

    }//GEN-LAST:event_jButton3ActionPerformed

